import React, { Component } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";

export default class UserRow extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movies: [],
    };
  }

  componentDidMount() {
    fetch("http://localhost:5555/userInfo", {
      method: "POST",
      crossDomain: true,
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      body: JSON.stringify({
        token: localStorage.getItem("token"),
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data, "userInfo");
        // console.log(data.data.movID);
        this.setState({ movies: data.data.movID });
      });
  }

  render() {
    const base_url = "https://image.tmdb.org/t/p/original/";
    var title = "User";
    var isLargeRow = false;
    return (
      <div className="row">
        <div className="category-header">
          <h2>USER LIKED</h2>
        </div>

        <div className="row__posters">
          {this.state.movies.map((movie, index) => (
            <>
              <img
                className={`row__poster  ${isLargeRow && "row__posterLarge"}`}
                key={movie.id}
                src={`${base_url}${
                  isLargeRow ? movie.poster_path : movie.backdrop_path
                }`}
                alt={movie.name}
                onClick={() => {
                  this.componentDidMount();
                }}
              ></img>
            </>
          ))}
        </div>

        <Modal className = "movie_popup" show={show} onHide={handleClose} >
          <Modal.Header closeButton>
            <Modal.Title> <b>{movie_name}</b> </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <img classNme = "img-fluid" src = {movie_poster}></img>
            <p>
              <b> Synopsis: </b> {movie_description}
            </p>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="danger" onClick={() => {
              AddDislikedMovie(movie_all);
              setShow(false);
            }}>
              Unlike
            </Button>
          </Modal.Footer>
        </Modal>

      </div>
    );
  }
}
