import React, {useState, useEffect, Component} from 'react';
import axios from './axios';
import './Row.css';
import App from './login.component';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import MovieDescription from './MovieDescription';


const base_url = "https://image.tmdb.org/t/p/original/";

var result;
function test(id){
   result = id;

   //console.log(result);
   var email = localStorage.getItem("email");         //retrieve the email from local storage
   var password = localStorage.getItem("password");   //retrieve the password from local storage
   var movID=id;
   console.log(email,password,movID);



   fetch("http://localhost:5000/login",{
    method: "POST",
    crossDomain: true,
    headers:{
      "Content-Type": "application/json",
      Accept: "application/json",
      "Access-Control-Allow-Origin": "*"
    },
    body: JSON.stringify({
      email,
      password,
      movID,
    }),
  }).then((res)=> res.json())
  .then((data)=>{
    console.log(data, "userRegister");
  })



};

function Description(id) {
    return (
        <Modal.Dialog>
            <Modal.Header closeButton>
                <Modal.Title>Modal title</Modal.Title>
            </Modal.Header>

            <Modal.Body>
                <p>Modal body text goes here.</p>
            </Modal.Body>

            <Modal.Footer>
                <Button variant="secondary">Close</Button>
                <Button variant="primary">Save changes</Button>
            </Modal.Footer>
        </Modal.Dialog>
    );
}


function Row({title, fetchUrl, isLargeRow}){
    const[movies, setMovies] = useState([]);

    //const [show, setShow] = useState(false);
    //const handleClose = () => setShow(false);
    //const handleShow = () => setShow(true);

    useEffect(()=>{

        async function fetchData(){
            const request = await axios.get(fetchUrl);
            console.log(request.data.results);
           // console.log('pizza testing');
            setMovies(request.data.results);
            return request;
        }
        fetchData();
    },[fetchUrl]);
//console.log('hello');

    console.log(movies);
   
    return (
        <div className="row">
            <div className = "category-header">
            <h2>{title}</h2>
            </div>
            
            <div className="row__posters">
                {movies.map((movie,index)=>(
                    <><img className={`row__poster  ${isLargeRow && "row__posterLarge"}`}
                        key={movie.id}
                        src={`${base_url}${isLargeRow ? movie.poster_path : movie.backdrop_path}`}
                        alt={movie.name}
                        //onClick = {() => test(movie.id)}

                        onClick = {() => Description(movie)}

                        //onClick  = {showModal ? <MovieDescription setShowModal={setShowModal} /> : null}


                        /*
                        onClick = {() => {
                         props.movieDescription(movie.id);
                         window.scrollTo({ top: 0, behavior: "smooth" });


                     }}

                         */
                        >
                        
                        </img>





                        </>




                        
                ))}




            </div>
        </div>
        
    )
    
}
export default Row

