import React, {Component, useState} from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";



export default class UserRow extends React.Component {

  _renderCounter = () => () => {
    const [show, setShow] = useState(false);
    return <div>
      {show}
    </div>
  }

  constructor(props) {
    super(props);
    this.state = {
      movies: [],
    };
  }

  componentDidMount() {
    fetch("http://localhost:5555/userInfo", {
      method: "POST",
      crossDomain: true,
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      body: JSON.stringify({
        token: localStorage.getItem("token"),
      }),
    })
        .then((res) => res.json())
        .then((data) => {
          console.log(data, "userInfo");
          // console.log(data.data.movID);
          this.setState({ movies: data.data.movID });
        });
  }

  render() {
    const base_url = "https://image.tmdb.org/t/p/original/";
    var title = "User Liked Movies";
    var isLargeRow = false;

    var movie_all;
    var movie_id = -1;
    var movie_description = "";
    var movie_name = "";
    var movie_poster = "";
    const API_IMG = "https://image.tmdb.org/t/p/w200";

    //const [show, setShow] = useState(false);
    //const handleClose = this.setState({ show: this.state.show: true });
    //const handleClose = () => setShow(false);

    const MyInlineHook = this._renderCounter();


    return (
        <div className="row">
          <div className="category-header">
            <h2>{title}</h2>
          </div>

          <div className="row__posters">
            {this.state.movies.map((movie, index) => (
                <>
                  <img
                      className={`row__poster  ${isLargeRow && "row__posterLarge"}`}
                      key={movie.id}
                      src={`${base_url}${
                          isLargeRow ? movie.poster_path : movie.backdrop_path
                      }`}
                      alt={movie.name}
                      onClick={() => {
                        //this.componentDidMount();
                        //movie_all = movie;
                        //movie_id = movie.id;
                        //movie_name = movie.title;
                        //movie_description = movie.overview;
                        //movie_poster = (API_IMG) + (movie.poster_path);
                        //setShow(true);
                        MyInlineHook().setShow(true);

                      }}
                  ></img>
                </>
            ))}
          </div>

          <Modal className = "movie_popup" show={show} onHide={handleClose} >
            <Modal.Header closeButton>
              <Modal.Title> <b>{movie_name}</b> </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <img classNme = "img-fluid" src = {movie_poster}></img>
              <p>
                <b> Synopsis: </b> {movie_description}
              </p>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>
                Close
              </Button>
              <Button variant="danger" onClick={() => {
                setShow(false);
              }}>
                Unlike
              </Button>
            </Modal.Footer>
          </Modal>

        </div>
    );
  }
}

